import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      calandars: [],
    };
  }

  componentDidMount() {
    fetch(`http://api.aladhan.com/v1/calendarByCity?country=France&method=2&city=Tourcoing`)
      .then((Response) => Response.json())
      .then((obj) => obj.data)
      .then(result => this.setState({calandars : result}))
  }

