  render() {
    return (
      <div className="App">
        <h1>Ok</h1>
        {
          //    this.state.monsters.map((monster, index )=> <h3 key={index}>{monster.name}</h3>)
          //console.log(this.state.calandars)
          this.state.calandars.map((obj) =>
         ( <div>
<h3>{obj.date.gregorian.date}</h3>
          <h3>{obj.timing.Fajr}</h3>
          <h3>{obj.timing.Sunrise}</h3>
          <h3>{obj.timing.Dhur}</h3>
          <h3>{obj.timing.Asr}</h3>
          <h3>{obj.timing.Maghrib}</h3>
          <h3>{obj.timing.Isha}</h3>

         </div>
         )
          ) 
        }
      </div>
    );
  }
}

export default App;
