import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      calandars: [],
    };
  }

  componentDidMount() {
    fetch(`http://api.aladhan.com/v1/calendarByCity?country=France&method=2&city=tourcoing`)
      .then((Response) => Response.json())
      .then((obj) => obj.data)
      .then((dataArray) =>
        dataArray.map((data) => {
          return {
            date: {
              gregorian: data.date.gregorian.date,
              hijri: data.date.hijri.date,
            },
            salat: {
              fajr: data.timings.Fajr,
              shourouk: data.timings.Sunrise,
              dhor: data.timings.Dhuhr,
              asr: data.timings.Asr,
              maghreb: data.timings.Maghrib,
              icha: data.timings.Isha,
              midnight: data.timings.Midnight,
            },
          };
        })
      )
  }

