import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: [],
      loading: true,
    };
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((users) => {
        this.setState({ monster: users, loading: false });
      })
      .catch((error) => {
        console.log("Fetch users error ", error);
      });
  }
  render() {
    return (
      <div className="App">
        {!this.state.loading && <CardList cards={this.state.monsters} />}
      </div>
    );
  }
}

export default App;
